INSERT INTO `asignaturademaestria` (`id`, `nombre`, `abreviatura`, `creditos`, `tipo`, `objetivos`, `contenidos`) VALUES 
  (1,'software de sistemas paralelos y distribuidos','spd',3,'opcional','explicar los conceptos y las técnicas fundamentales utilizadas en el software de los sistemas paralelos y distribuidos','técnicas de programación paralela. sistemas distribuidos. comunicación, sincronización, procesos y procesadores en sistemas distribuidos. sistemas distribuidos de archivos. aspectos del diseño de los sistemas distribuidos'),
  (2,'taller de ingeniería y gestión de software','tis',3,'opcional','que los estudiantes profundicen en conocimientos especializados de la ingeniería y gestión de software; así como desarrollen habilidades para su aplicación en proyectos de investigación o innovación en dicho campo','temas especializados actuales en ingeniería y gestión de software, vinculados a los proyectos de investigación e innovación que desarrollen los estudiantes del taller.'),
  (3,'matemáticas discretas ','md',3,'opcional','profundizar en los principales conceptos de algunos temas de matemáticas discretas (teoría de conjuntos, relaciones, teoría de grafos y lógica matemática), los cuales son fundamentales para la comprensión de otras áreas de la informática aplicada. ','teoría de conjuntos. operaciones. comparación de magnitudes infinitas, cardinal de un conjunto. relaciones. operaciones. grafos. árboles. cálculo proposicional. cálculo de predicados de primer orden. resolución'),
  (4,'taller de gestión de tecnologías de la información','tgt',3,'opcional','que los estudiantes profundicen en conocimientos especializados en gestión de tecnologías de la información; así como desarrollen habilidades para su aplicación en proyectos de investigación o innovación en dicho campo','temas especializados actuales de la gestión de tecnologías de la información, vinculados a los proyectos de investigación e innovación que desarrollen los estudiantes del taller'),
  (5,'bases de datos ','bd',3,'basica','profundizar en las técnicas de diseño de bases de datos y su implementación en los sistemas de gestión de bases de datos (sgbd). describir las características fundamentales de las bases de datos deductivas, distribuidas y orientadas a objetos','modelo conceptual. modelo relacional extendido. bases de datos deductivas. bases de datos distribuidas. concurrencia. protección y seguridad. bases de datos orientadas a objetos. modelo relacional con modelos orientados a objetos'),
  (6,'seminario de investigación e innovación tecnológica','sit',3,'forminvest','que los estudiantes se apropien de los conceptos básicos y métodos generales para el trabajo de investigación e innovación, así como su utilización en el campo de la informática y sus aplicaciones','fundamentos teóricos de la investigación científica y la innovación tecnológica. teoría del conocimiento como base metodológica de la investigación y la innovación. trabajo con las fuentes bibliográficas. el problema de investigación o innovación. objeto y campo de acción. la hipótesis y preguntas científicas. métodos de investigación e innovación. el proyecto de investigación-desarrollo e innovación tecnológica a ciclo completo. la evaluación económica de costos y beneficios de un trabajo de investigación-desarrollo e innovación tecnológica. evaluación de impactos científico-tecnológicos, económicos, sociales y medioambientales'),
  (7,'arquitecturas de computadoras y redes','acr',3,'basica','describir las diferentes arquitecturas de redes de computadoras, los principios para la administración de las mismas y ser capaz de explotar sus prestaciones. describir los fundamentos de las comunicaciones mediante computadoras, los principales protocolos de enlace de datos y los principios de las teorías de la información y de la codificación.','arquitectura y administración de redes de computadoras. teorías de la información y la codificación. comunicaciones de datos. protocolos de comunicación'),
  (8,'taller de sistemas informáticos aplicados','tsi',3,'opcional','que los estudiantes profundicen en conocimientos especializados en un campo de la informática aplicada, vinculado a sus proyectos de investigación o innovación; así como desarrollen habilidades para su aplicación en dicho proyecto','temas especializados actuales en campos de la informática aplicada, vinculados a los proyectos de investigación e innovación que desarrollen los estudiantes del taller'),
  (9,'ingeniería de software ','is',3,'basica','profundizar en las distintas etapas del ciclo de vida de un proyecto informático. describir y utilizar metodologías, instrumentos y técnicas para el desarrollo de un sistema informático. aplicar los conceptos y técnicas actuales al diseño de la interfaz hombre-máquina de un sistema informático','ciclo de vida de un proyecto informático. metodologías, instrumentos y técnicas para el desarrollo de un sistema informático. estudio comparativo de metodologías. interfaz hombre-máquina y  técnicas para su diseño.'),
  (10,'taller de informática educativa ','tie',3,'opcional','que los estudiantes profundicen en conocimientos especializados de la informática educativa y desarrollen habilidades para su aplicación en proyectos de investigación o innovación en dicho campo','temas especializados actuales en informática educativa, vinculados a los proyectos de investigación e innovación que desarrollen los estudiantes del taller'),
  (11,'sistemas informáticos inteligentes','sii',3,'basica','explicar los campos de acción de la inteligencia artificial. describir las generalidades de la representación del conocimiento y las diferencias entre los principales formalismos de representación y procesamiento. describir los fundamentos del razonamiento con incertidumbre y del aprendizaje','campos de acción de la inteligencia artificial. representación y procesamiento del conocimiento, formalismos y lenguajes para el procesamiento simbólico. sistemas expertos. procesamiento bajo incertidumbre. aprendizaje automático, redes neuronales y algoritmos evolutivos'),
  (12,'compresión y cifrado de datos ','ccd',3,'opcional','interpretar y describir los algoritmos fundamentales utilizados para la compresión y cifrado de datos. interpretar y describir los criptosistemas de clave privada y de clave pública; así como los modos de cifrados en redes de computadoras. se pretende además desarrollar habilidades para la programación de algoritmos en este campo','compresión de datos. compresión de textos e imágenes. algoritmos básicos. criptografía. criptosistemas de clave privada y de clave pública. algoritmos. modos de cifrado en redes de computadoras'),
  (13,'fundamentos de la informática educativa ','fie',3,'opcional','describir los fundamentos pedagógicos y tecnológicos que sustentan la informática educativa. aplicar tales fundamentos al estudio y desarrollo de componentes y sistemas informáticos para su utilización en la educación','análisis de las tendencias pedagógicas contemporáneas desde la perspectiva de la informática educativa. didáctica e informática educativa. estructura y funcionamiento de diferentes tipos de sistemas para la informática educativa. técnicas para la construcción de componentes y sistemas informáticos para la educación'),
  (14,'taller de auditoría y seguridad informática','tas',3,'opcional','que los estudiantes profundicen en conocimientos especializados de la auditoría y seguridad informática; así como desarrollen habilidades para su aplicación en proyectos de investigación o innovación en dicho campo','temas especializados actuales de auditoría y seguridad informática, vinculados a los proyectos de investigación e innovación que desarrollen los estudiantes del taller'),
  (15,'taller de procesamiento paralelo y distribuido','tpd',3,'opcional','que los estudiantes profundicen en conocimientos especializados de software de los sistemas paralelos y distribuidos; así como desarrollen habilidades para su aplicación en proyectos de investigación o innovación en dicho campo','temas especializados actuales sobre el software de los sistemas paralelos y distribuidos, vinculados a los proyectos de investigación e innovación que desarrollen los estudiantes del taller'),
  (16,'taller de inteligencia artificial ','tia',3,'opcional','que los estudiantes profundicen en conocimientos especializados en inteligencia artificial y desarrollen habilidades para su aplicación en proyectos de investigación o innovación en dicho campo','temas especializados actuales en inteligencia artificial, vinculados a los proyectos de investigación e innovación que desarrollen los estudiantes del taller'),
  (17,'calidad del software ','cs',3,'opcional','valorar la calidad de un software por medio de la aplicación de las métricas correspondientes. desarrollar software con garantía de su calidad. explicar instrumentos a utilizar para la garantía de la calidad','calidad del software. garantía de la calidad del software. revisiones del software. métricas de la calidad. fiabilidad del software. un enfoque para la garantía de la calidad. métricas de la calidad para proyectos estructurados. métricas de calidad para proyectos orientados a objetos. instrumentos case. discusión de las aplicaciones realizadas y los instrumentos de software utilizados'),
  (18,'gestión de proyectos informáticos','gpi',3,'opcional','realizar la planificación para el desarrollo de un proyecto informático teniendo en cuenta las diferentes métricas disponibles. llevar a cabo la planificación y el control de un proyecto informático. realizar el análisis de costo-beneficio de un proyecto informático y a partir de esto determinar el efecto económico. aplicar al software desarrollado los distintos tipos de pruebas para detectar errores y depurar su funcionamiento. describir las características y tareas del mantenimiento. desarrollar configuraciones del software, llevando el control de versiones y  cambios','plan del proyecto. métricas. estimación del proyecto. modelos de estimación. planificación operativa del proyecto. control operativo del proyecto. control de costos. cálculo de indicadores de planificación. fundamentos de la prueba de software. distintos tipos de pruebas. características y tareas del mantenimiento. gestión configuraciones. control de versiones. control de cambios. instrumentos case. estudio de casos'),
  (19,'matemática computacional ','mc',3,'opcional','profundizar en los conceptos básicos de la teoría matemática de las ciencias de la computación, con énfasis en la computabilidad, la decidibilidad, la recursividad, el diseño y análisis de algoritmos y los conceptos relacionados con ellos. se pretende que los alumnos se apropien de estos conceptos para su posterior utilización en otros cursos y en el trabajo práctico','computabilidad, decidibilidad, recursividad. complejidad computacional.  modelos de cómputo. análisis de complejidad. técnicas para el diseño de algoritmos.  clases de complejidad de problemas: p, np y problemas intrínsicamente exponenciales. introducción al análisis de algoritmos paralelos'),
  (20,'seguridad, riesgos y auditoría informática','sra',3,'opcional','valorar los riesgos comerciales, técnicos y de proyecto del software. aplicar las tecnologías para la seguridad en el desarrollo del software. explicar los abusos informáticos y las medidas para evitar la ocurrencia. describir los criterios de evaluación de la seguridad en los sistemas informáticos. describir los aspectos fundamentales referentes a la auditoría informática y las herramientas para su uso. participar en la auditoría a un sistema informático en estado de proyección. participar en la auditoría a un sistema informático en explotación','identificación y evaluación del riesgo. plan de gestión y de supervisión del riesgo. seguridad física y administrativa. vulnerabilidad y mecanismo de seguridad en redes. tecnología para la seguridad. criterios de evaluación de los sistemas de información. auditoría informática. auditoría y control. herramientas de auditoría informática. auditoría al sistema de protección y seguridad. metodología de la auditoría'),
  (21,'taller de investigación e innovación tecnológica','tit',17,'forminvest','con estas actividades, vinculadas a la ejecución del proyecto de cada estudiante, se persigue aumentar el nivel de habilidades creadas en los seminarios para el trabajo de investigación-desarrollo e innovación tecnológica; integrando los conocimientos, habilidades y valores que vaya alcanzando el estudiante según avance en el programa, encaminado al proceso de elaboración y defensa de una memoria para la evaluación final. por esta razón, el taller funcionará a través de varias actividades integradoras distribuidas a lo largo de todo el cronograma de desarrollo de la maestría','temas de la línea de investigación-desarrollo o innovación tecnológica, propios de cada estudiante.  orientaciones metodológicas para la escritura de la memoria para la evaluación final de la maestría. exposición y defensa de la memoria escrita, previo a la evaluación final de la maestría');
